let slider_image=document.getElementById("slider_image");

var image=["./banner/8.jpg","./banner/3.jpeg","./banner/2.jpeg","./banner/4.jpeg"];
    var i=0;
    function slideshow(){
        slider_image.src=image[i];

        if(i<image.length-1){
            i++;
        }else{
            i=0;
        }
        setTimeout("slideshow()",2000);
    }
    window.onload=slideshow;
   

   

