let Sadasya=document.getElementById('Sadasya');
// console.log(Sadasya)
  allmandal(Mandal_Details)
//   console.log(Mandal_Details[1].img)

  
  function allmandal(data){
    let allphotos=""
//   console.log(data.length)
    for(let i=0;i<data.length;i++){
        
        allphotos=allphotos+`

            <div>
                    <div>
                    <img src=${data[i].img} alt=${data[i].id}>
                    </div>
                    <p> ${data[i].name}</p>
                    <h5> ${data[i].title}</h5>
                   

                </div>
        
        
        
        `
       Sadasya.innerHTML=allphotos
    }
      
    
}

let tbody=document.getElementById("tbody");
table(event_Details)
 

function table(data){
    let allphotos=""
//   console.log(data.length)
    for(let i=0;i<data.length;i++){
        
        allphotos=allphotos+`

    <tr>
        <td>${data[i].id}</td>
        <td>${data[i].name}</td>
        <td>${data[i].Date}</td>
        <td>${data[i].time}</td>
    </tr>
        
        
        `
       tbody.innerHTML=allphotos
    }
      
    
}

