// Gallery details works
let photo_btn=document.getElementById('photo_btn');
let video_btn=document.getElementById('video_btn');
let photo_div=document.getElementById('photo_div');
let video_div=document.getElementById('video_div');
// console.log(photo_btn,photo_div,video_btn,video_div);

video_btn.addEventListener('click',()=>{
    photo_div.style.display="none";
    video_div.style.display="grid";
    video_btn.style.width="50%"
    video_btn.style.height="4vh";
    photo_btn.style.height="4vh";
    photo_btn.style.width="40%";
    video_btn.style.transition="1s";
    video_btn.style.backgroundSize="cover";
    photo_btn.style.backgroundImage="url()";
    video_btn.style.backgroundImage="url(data:image/jpeg;base64,)"

})

photo_btn.addEventListener('click',()=>{
    video_div.style.display="none";
    photo_div.style.display="grid";
    photo_btn.style.width="50%"
    photo_btn.style.height="4vh";
    video_btn.style.width="40%"
    video_btn.style.height="4vh";
    photo_btn.style.transition="1s";
    photo_btn.style.backgroundSize="cover";
    video_btn.style.backgroundImage="url()"
    photo_btn.style.backgroundImage="url(data:image/jpeg;base64,)"

})

let url = `https://652cbdf3d0d1df5273ef9f4a.mockapi.io/details`;


fetch(url)
  .then((res) => {
    return res.json();
  })
  .then((data) => {
    
    // gallery_details_API = data;
    // console.log(gallery_details_API)
    photo_function(data)
    video_function(data)
  
  })
  .catch((err) => {
    console.log(err);
  });



  function photo_function(data){
    let allphotos=""
//   console.log(data.length)
    for(let i=0;i<data.length;i++){
        console.log(data[i].video)
        
        allphotos=allphotos+`

    <div>
        
      
      ${data[i].video!=""? ` <iframe  src=${data[i].video} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe> 
      `:""}
       
     
       
         
        
      
    </div>
        
        
        `
        video_div.innerHTML=allphotos
    }

   
      
    
}

function video_function(data){
    let allphotos=""
//   console.log(data.length)
    for(let i=0;i<data.length;i++){
        
        allphotos=allphotos+`

    <div>
        
        <img src=${data[i].avatar} alt=${data[i].id}>
         
        
      
    </div>
        
        
        `
        photo_div.innerHTML=allphotos
    }

   
      
    
}